export function MoodIcons({ mood, size = 32 }: MoodIconProps) {
  switch (mood) {
    case "tough":
      return <ToughIcon size={size} />;
    case "good":
      return <GoodIcon size={size} />;
    case "amazing":
      return <FireIcon size={size} />;
    default:
      return <GoodIcon size={size} />;
  }
}

export function SkipIcons({ mood, size = 32 }: SkipIconProps) {
  switch (mood) {
    case "work":
      return <WorkIcon size={size} />;
    case "tired":
      return <TiredIcon size={size} />;
    case "sick":
      return <SickIcon size={size} />;
    case "schedule":
      return <ScheduleIcon size={size} />;
    case "other":
      return <ThinkingIcon size={size} />;
    default:
      return <WorkIcon size={size} />;
  }
}
